import React from "react";

class App extends React.Component {
  render() {
    const students = [
      {
        promo: "A",
        name: "Sofía",
        age: 20,
      },
      {
        promo: "B",
        name: "María",
        age: 21,
      },
      {
        promo: "A",
        name: "Lucía",
        age: 22,
      },
    ];

    const filterStudents = students.filter((student) => {
      return students.promo === "A";
    });

    return (
      <div>
        {filterstudents.map((students) => {
          return (
            <li key={students.name}>
              <p>Nombre: {students.name}</p>
              <p>Edad: {students.age}</p>
            </li>
          );
        })}
      </div>
    );
  }
}

export default App;
