import React from "react";

class OnionHater extends React.Component {
  constructor(props) {
    super(props);
    this.handleTextArea = this.handleTextArea.bind(this);
    this.isHating = false;
  }

  handleTextArea(event) {
    if (event.target.value.includes("cebolla") === true) {
      this.isOnion = true;
    } else {
      this.isOnion = false;
    }
    this.forceUpdate();
  }

  render() {
    if (this.isOnion === true) {
      return (
        <div className="page red">
          <textarea
            className="textarea"
            name="textarea"
            id="textarea"
            onChange={this.handleTextArea}
          ></textarea>
        </div>
      );
    } else {
      <div className="page">
        <textarea
          className="textarea"
          name="textarea"
          id="textarea"
          onChange={this.handleTextArea}
        ></textarea>
      </div>;
    }
  }
}
export default OnionHater;
